mapper.select:
select count(*) from t_sys_store where is_deleted = 0
and store_id != #{storeId}
and user_id = #{storeManagerId}

select a.store_id storeId, a.store_name storeName, a.store_phone storePhone, a.store_province_id storeProvinceId, a.store_city_id storeCityId, a.store_area_id storeAreaId, a.store_address storeAddress, b.name storeManagerName, a.invite_code inviteCode, b.user_account storeAccount, a.version from t_sys_store a,t_sys_user b,t_sys_dictionary c,t_sys_dictionary d,t_sys_dictionary e where a.user_id = b.user_id and a.is_deleted = 0 and b.is_deleted = 0 and a.store_province_id = c.dictionary_id and a.store_city_id = d.dictionary_id and a.store_area_id = e.dictionary_id
and a.user_id = #{userId}
and a.store_id = #{storeId}
and a.store_name like concat('%',#{storeName},'%')
and b.name like concat('%',#{storeManagerName},'%')
and a.store_province_id = #{storeProvinceId}
and a.store_city_id = #{storeCityId}
and a.store_area_id = #{storeAreaId}
order by a.gmt_create desc
